package me.michidk.FakeMCServer;

import java.io.File;
import java.net.InetAddress;
import java.net.ServerSocket;
import java.util.Collections;
import java.util.LinkedList;
import java.util.logging.ConsoleHandler;
import java.util.logging.Logger;
import java.util.regex.Pattern;

/**
 * @author michidk
 */

public class Main
{

    public static final String version = "v1.0";
    public static final Logger log = Logger.getLogger("FakeMCServer");


    public static int port = 25565;
    public static String ip = "localhost";
    public static String icon;
    public static String verText;
    public static String motd;
    public static String players = "asdas";
    public static Integer maxPlayers;
    public static String kickMessage = "\u0000";

    public static ServerSocket server;
    public static final String blankIcon = "data:image/png;base64,";


    public static void main( String[ ] args )
    {
        ConsoleHandler handler = new ConsoleHandler();
        handler.setFormatter(new LogFormatter());
        log.addHandler(handler);
        log.setUseParentHandlers(false);

        log.info("     ==> FakeMCServer " + version + " by michidk <==");
        log.info("Github: https://github.com/michidk/FakeMCServer");
        log.info("YouTube: https://www.youtube.com/user/CodeBukkit");
        log.info("-----------------------------------------------");


        //parse args
        LinkedList<String> arguments = new LinkedList<String>( );
        Collections.addAll(arguments, args);

        while(!arguments.isEmpty())
        {
            String arg = (String) arguments.pop();

            if (arg.equalsIgnoreCase("-ip"))
            {
                ip = (String) arguments.pop();
            }
            else if (arg.equalsIgnoreCase("-port"))
            {
                port = Integer.parseInt((String) arguments.pop());
            }
        }

        loadResources();
        addShutdownHook();
        startServer();

    }

    public static void loadResources()
    {
        log.info("loading resources...");

        //load servericon
        final String iconPath = "server-icon.png";
        File serverIcon = new File(iconPath);
        if (!serverIcon.exists())
        {
            log.warning("no '" + iconPath + "' found");
        }
        else
        {
            String base64 = FileHelper.decodeBase64(serverIcon);
            if (base64 == null || base64 == "")
            {
                log.warning("something went wrong while decoding '" + iconPath + "'");
            }
            else
            {
                icon = "data:image/png;base64," + base64;
                log.info("'" + iconPath + "' successfully loaded");
            }
        }

        //load version
        File verTextFile = new File("version.txt");
        if (!verTextFile.exists())
        {
            log.warning("no 'version.txt' found");
        }
        else
        {
            verText = parseColors(FileHelper.stringFromFile(verTextFile));

            log.info("'version.txt' successfully loaded");

        }

        //load motd
        File motdFile = new File("motd.txt");
        if (!motdFile.exists())
        {
            log.warning("no 'motd.txt' found");
        }
        else
        {
            motd = parseColors(FileHelper.stringFromFile(motdFile));

            log.info("'motd.txt' successfully loaded");

        }

        //kick message
        File kickMessageFile = new File("kickmessage.txt");
        if (!kickMessageFile.exists())
        {
            log.warning("no 'kickmesssage.txt' found");
        }
        else
        {
            kickMessage = parseColors(FileHelper.stringFromFile(kickMessageFile));

            log.info("'kickmessage.txt' successfully loaded");

        }

        //players
        File playersFile = new File("players.txt");
        if (!playersFile.exists())
        {
            log.warning("no 'players.txt' found");
        }
        else
        {
            players = parseColors(FileHelper.stringFromFile(playersFile));
            log.info("'players.txt' successfully loaded");
        }

        //maxplayers
        File maxPlayersFile = new File("maxplayers.txt");
        if (!maxPlayersFile.exists())
        {
            log.warning("no 'maxplayers.txt' found");
        }
        else
        {
            try
            {
                maxPlayers = Integer.parseInt(FileHelper.stringFromFile(maxPlayersFile));
                log.info("'maxplayers.txt successfully loaded'");
            }
            catch (NumberFormatException e)
            {
                log.warning("invalid 'maxplayers.txt'");
                maxPlayers = null;
            }
        }

    }

    public static void addShutdownHook()
    {
        Runtime.getRuntime().addShutdownHook(new Thread()
        {
            public void run()
            {
                stopServer();
            }
        } );
    }

    public static void startServer()
    {
        log.info("starting server...");

        try
        {
            InetAddress adress = (ip == null ? InetAddress.getLocalHost():InetAddress.getByName(ip));
            server = new ServerSocket(port, 10, adress);
            log.info("server started on " + adress.getHostAddress()+ ":" + port);

            while(!server.isClosed())
            {
               new ResponderThread(server.accept()).start();
            }

        }
        catch(Exception e)
        {
            log.warning("failed to start the server on " + ip + ":" + port);
            log.severe("error: " + e.getMessage());

            System.exit(0);
        }

        stopServer();

    }

    public static void stopServer()
    {
        if (server == null) return;
        log.info("stopping server...");


        try
        {
            server.close();
        }
        catch(Exception e)
        {
            log.severe("failed to stop server: " + e.getMessage());
            return;
        }

        log.info("server stopped");
    }

    private static Pattern colorPattern = Pattern.compile("&([0-9a-fk-or])");
    public static String parseColors(String message)
    {
        return colorPattern.matcher(message).replaceAll("\u00a7$1");
    }

}
