package me.michidk.FakeMCServer;

import java.io.File;
import java.io.IOException;
import java.net.InetAddress;
import java.net.ServerSocket;
import java.util.ArrayList;
import java.util.List;
import java.util.logging.ConsoleHandler;
import java.util.logging.Logger;
import java.util.regex.Pattern;

/**
 * @author michidk
 */

public class Main
{

    public static final String version = "v1.0";
    public static final Logger log = Logger.getLogger("FakeMCServer");


    public static          String   host        = null;
    public static          Integer  port        = null;
    public static volatile String   icon        = null;
    public static volatile String   verText     = null;
    public static volatile String   motd        = null;
    public static volatile String[] players     = null;
    public static volatile Integer  maxPlayers  = null;
    public static volatile String   kickMessage = null;

    public static volatile ServerSocket server = null;
    public static final String blankIcon = "data:image/png;base64,";


    public static void main(final String[] args)
    {
        handler.setFormatter(new LogFormatter());
        log.addHandler(handler);
        log.setUseParentHandlers(false);

        log.info("==> FakeMCServer " + version + " by xxmicloxx and michidk <==");
        log.info("Github: https://github.com/michidk/FakeMCServer");
        log.info("YouTube: https://www.youtube.com/CodeBukkit");
        log.info("-----------------------------------------------");


        //parse args
        LinkedList<String> arguments = new LinkedList<String>( );
        Collections.addAll(arguments, args);

        while(!arguments.isEmpty())
        {
            String arg = (String) arguments.pop();

            if (arg.equalsIgnoreCase("-ip"))
            {
                ip = (String) arguments.pop();
            }
            else if (arg.equalsIgnoreCase("-port"))
            {
                port = Integer.parseInt((String) arguments.pop());
            }
        }

        final ConsoleHandler handler = new ConsoleHandler();
        loadResources();
        addShutdownHook();
        startServer();

    }

    public static void loadResources()
    {
        log.info("loading resources...");

        //load servericon
        final String iconPath = "server-icon.png";
        final File serverIcon = new File(iconPath);
        if (!serverIcon.exists())
        {
            log.warning("icon file '" + iconPath + "' not found");
            icon = null;
        }
        else
        {
            final String base64 = FileHelper.decodeBase64(serverIcon);
            if (base64 == null || base64.isEmpty())
            {
                log.warning("something went wrong while decoding '" + iconPath + "'");
                icon = null;
            }
            else
            {
                icon = "data:image/png;base64," + base64;
                log.info("icon file '" + iconPath + "' successfully loaded");
            }
        }

        //load version
        {
            final String str = getFileString("version.txt");
            verText = (str == null ? null : parseColors(str));
        }
        //load motd
        {
            final String str = getFileString("motd.txt");
            motd = (str == null ? null : parseColors(motd));
        }
        //kick message
        {
            final String str = getFileString("kickmessage.txt");
            kickMessage = (str == null ? null : parseColors(str));
        }
        //players
        {
            final String str = getFileString("players.txt");
            players = (str == null ? null : parseColors(str));
        }
        //maxplayers
        {
            final String str = getFileString("maxplayers.txt");
            try {
                maxPlayers = (str == null ? null : Integer.parseInt(str));
            }
            catch (NumberFormatException e)
            {
                log.warning("invalid number in 'maxplayers.txt'");
                maxPlayers = null;
            }
        }

    }

    private static String getFileString(final String fileName)
    {
        final File file = new File(fileName);
        if(!file.isFile()) {
            try {
                file.createNewFile();
                log.warning("created empty file '"+fileName+"'");
            }
            catch (IOException e)
            {
                log.warning("file '"+fileName+"' not found");
                e.printStackTrace();
            }
            return null;
        }
        if(!file.canRead()) {
            log.warning("file '"+fileName+"' not readable");
            return null;
        }
        final String str = FileHelper.stringFromFile(file);
        if(str == null || str.isEmpty()) {
            log.warning("file '"+fileName+"' is empty");
            return null;
        }
        log.info("file '"+fileName+"' successfully loaded");
        return str;
    }

    public static void addShutdownHook()
    {
        Runtime.getRuntime().addShutdownHook(new Thread()
        {
            public void run()
            {
                stopServer();
            }
        } );
    }

    public static void startServer()
    {
        log.info("starting server...");

        try
        {
            InetAddress adress = (ip == null ? InetAddress.getLocalHost():InetAddress.getByName(ip));
            server = new ServerSocket(port, 10, adress);
            log.info("server started on " + adress.getHostAddress()+ ":" + port);

            while(!server.isClosed())
            {
               new ResponderThread(server.accept()).start();
            }

        }
        catch(Exception e)
        {
            log.warning("failed to start the server on " + ip + ":" + port);
            log.severe("error: " + e.getMessage());

            System.exit(0);
        }

        stopServer();

    }

    public static void stopServer()
    {
        if (server == null) return;
        log.info("stopping server...");


        try
        {
            server.close();
        }
        catch(Exception e)
        {
            log.severe("failed to stop server: " + e.getMessage());
            return;
        }

        log.info("server stopped");
    }

    private static Pattern colorPattern = Pattern.compile("&([0-9a-fk-or])");
    public static String parseColors(final String message)
    {
        if(message == null) return null;
        return colorPattern.matcher(message).replaceAll("\u00a7$1");
    }

}
